package java_Basic_Program_Most;

public class Division {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3, b=10;
		int c = b%a;
		if(c==1)
		{
			System.out.println("We are using here division");
		}
		else
		{
			System.out.println("We are using here modulus");
		}
	}
}
