package java_Basic_Program_Most;

public class Print_ASCII_Value_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char char1 = 'a';
		char char2 = 'b';
		char char3 = 'c';
		
		int ASCII_1 = char1;
		int ASCII_2 = char2;
		int ASCII_3 = char3;
		
		System.out.println("ASCII Value of " + char1 + " is: " + ASCII_1);
		System.out.println("ASCII Value of " + char2 + " is: " + ASCII_2);
		System.out.println("ASCII Value of " + char3 + " is: " + ASCII_3);

	}
}
