package java_Basic_Program_Most;

public class Print_ASCII_Value_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char char1 = 'a';
		char char2 = 'b';
		char char3 = 'c';
		
		int ascii_1 = (int)char1;
		int ascii_2 = (int)char1;
		int ascii_3 = (int)char1;
		
		System.out.println("ASCII Value of " + char1 +" is: " + ascii_1);
		System.out.println("ASCII Value of " + char2 +" is: " + ascii_2);
		System.out.println("ASCII Value of " + char3 +" is: " + ascii_3);
	}
}
